import { Add, Remove } from '@mui/icons-material'
import React from 'react'
import styled from 'styled-components'
import Announcement from '../compenents/Announcement'
import Footer from '../compenents/Footer'
import Navbar from '../compenents/Navbar'
import NewsLetter from '../compenents/NewsLetter'

const Container = styled.div`

`

const Wrapper = styled.div`
    padding: 20px;
    margin-bottom: 40px;
`

const Title = styled.h1`
    font-weight: 300;
    text-align: center;
`

const Top = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 20px;

    @media only screen and (max-width:980px){
        zoom: 70%;
    }
`

const TopButton = styled.button`
    padding: 10px;
    font-weight: 600px;
    cursor: pointer;
    border: ${ props => props.type === "filled" && "none" };
    background-color: ${ props => 
        props.type === "filled" ? "black" : "transparent" };
    color: ${ props => props.type === "filled" && "white" };

`



const TopTexts = styled.div`
    @media only screen and (max-width:980px){
        flex
    }
`

const TopText = styled.span`
    text-decoration: underline;
    cursor: pointer;
    margin: 0px 10px;
`

const Bottom = styled.div`
    display: flex;
    justify-content: space-between;

    @media only screen and (max-width:480px){
        flex-direction: column;
    }

`

const Info = styled.div`
    flex: 3;
`

const Product = styled.div`
    display: flex;
    justify-content: space-between;
`

const ProductDetail = styled.div`
    flex: 2;
    display: flex;
`

const Image = styled.img`
 width: 200px;

 @media only screen and (max-width:980px){
        width: 100px;
    }
`

const Details = styled.div`
    padding: 20px;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
`

const ProductName = styled.span`

`

const ProductId = styled.span`

`

const ProductColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color:  ${ props => props.color } ;
`

const ProductSize = styled.span`

`

const PriceDetails = styled.span`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`

const ProductAmountContainer = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 20px;

`

const ProductAmount = styled.div`
    font-size: 24px;
    margin: 5px;
`

const ProductPrice = styled.div`
    font-size: 30px;
    font-weight: 200;
`

const Hr = styled.hr`
    background-color: #eee;
    border: none;
    height: 1px;
`

const Summary = styled.div`
    flex: 1;
    border: 0.5px solid lightgray;
    border-radius: 10px;
    padding: 20px;
    height: 50vh;

    @media only screen and (max-width:980px){
        margin-top: 15px;
    }
`

const SummaryTitle = styled.h1`
    font-weight: 200;
`

const SummaryItem = styled.div`
    margin: 30px 0px;
    display: flex;
    justify-content: space-between;
    font-weight: ${ props => props.type === "total" && "500" } ;
    font-size: ${ props => props.type === "total" && "24px" } ;

`

const SummaryItemText = styled.span`

`

const SummaryItemPrice = styled.span`

`

const SummaryButton = styled.button`
    width: 100%;
    padding: 10px;
    background-color: black;
    color: #fff;
    font-weight: 600;
    cursor: pointer;
`


function Cart() {
  return (
    <Container>
        <Announcement />
        <Navbar />
        
            <Wrapper>

                <Title>YOUR BAG</Title>

                <Top>

                    <TopButton>CONTINUE SHOPPING</TopButton>
                    <TopTexts>
                        <TopText>Shopping Your Bag (2)</TopText>
                        <TopText>Your Whishlist (0)</TopText>
                    </TopTexts>
                    <TopButton type='filled'>CHECHOUT NOW</TopButton>

                </Top>

                <Bottom>

                    <Info>

                        <Product>

                            <ProductDetail>

                                <Image src="https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1614188818-TD1MTHU_SHOE_ANGLE_GLOBAL_MENS_TREE_DASHERS_THUNDER_b01b1013-cd8d-48e7-bed9-52db26515dc4.png?crop=1xw:1.00xh;center,top&resize=480%3A%2A"></Image>

                                <Details>

                                    <ProductName> <b>Product:</b> JESSIE THUNDER SHOES </ProductName>
                                    <ProductId> <b>ID:</b> 927340236402 </ProductId>
                                    <ProductColor color="black" />
                                    <ProductSize> <b>SIZE:</b> 36.5 </ProductSize>

                                </Details>

                            </ProductDetail>

                            <PriceDetails>

