import { ArrowLeftOutlined, ArrowRightOutlined } from '@mui/icons-material'
import React from 'react'
import "./Slider.css"
import styled from 'styled-components'

const Container = styled.div`
    margin-top: 30px;
    width: 100%;
    height: 100vh;
    display: flex;
    background-color: coral;
    position: relative;
`

const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: #fff7f7;
    border-radius: 50%;
    
    display: flex;
    justify-content: center;
    align-items: center;

    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props=> props.direction === "left" && "10px"};
    right: ${props=> props.direction === "right" && "10px"};
    margin: auto;  

    cursor: pointer;
    opacity: 0.5;
`

const Wrapper = styled.div`
    height: 100%;
`

const Slide = styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
`

const ImgContainer = styled.div`
    height: 100%;
    flex: 1;
`

const Image = styled.img`
    height: 80%;
`

const InfoContainer = styled.div`
    flex: 1;
    padding: 50px;
`

const Title = styled.h1`
    font-size: 70px;
`

const Desc = styled.p`
    margin: 50px 0px;
    font-size: 20px;
    font-weight: 500;
    letter-spacing: 3px;
`

const Button = styled.button`
    padding: 10px;
    font-size: 20px;
`



function Slider() {
  return (
    <div className='slider'>
        <Container>

            <Arrow direction="left">
                <ArrowLeftOutlined />
            </Arrow>

            <Wrapper>
                <Slide>

                    <ImgContainer>
                        <Image src="https://micopon.ir/wp-content/uploads/2021/09/imgbin_shopping-stock-photography-woman-stock-illustration-png.png" />
                    </ImgContainer>

                    <InfoContainer>
                        <Title>SUMMER SAL</Title>
                        <Desc>DON'T COMPROMÄ°SE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS.</Desc>
                        <Button>SHOP NOW</Button>
                    </InfoContainer>

                </Slide>
                
            </Wrapper>

            <Arrow direction="right">
                <ArrowRightOutlined />
            </Arrow>

        </Container>

    </div>
  )
}

export default Slider
