import { Facebook, Instagram, LinkedIn, Twitter } from '@mui/icons-material'
import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    display: flex;
`

const Left = styled.div`
    flex:1;
    display: flex;
    flex-direction: column;
    padding: 20px;
`

const Logo = styled.h1`
    
`

const Desc = styled.p`
    margin: 20px 0px;
`

const SocialContainer = styled.div`
    display: flex;
`

const SocialIcon = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    color: white;
    background-color: #${ props => props.color };

    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 20px;
`

const Center = styled.div`
    flex:1;
    padding: 20px;
    align
`

const Title = styled.h3`
    margin-bottom: 30px;
`

const List = styled.ul`
    margin: 0;
    padding: 0;
    list-style: none;
    display: flex;
    flex-wrap: wrap;
`

const ListItem = styled.li`
    width: 50%;
    margin-bottom: 10px;
`

const Right = styled.div`
    flex:1;
`



function Footer() {
  return (
    <Container>

        <Left>
            <Logo>Lama.</Logo>

            <Desc>Lorem ipsum dolor sit amet consectetur adipisicing elit. Distinctio earum voluptatum 
                temporibus aspernatur laboriosam cumque, amet excepturi. Rerum hic necessitatibus iste, possimus labore 
                dignissimos, suscipit a odio ipsum maxime expedita.
            </Desc>

            <SocialContainer>

                <SocialIcon color="3B5999">
                    <Facebook />
                </SocialIcon>

                <SocialIcon color="E4405F">
                    <Instagram />
                </SocialIcon>

                <SocialIcon color="55ACEE">
                    <Twitter />
                </SocialIcon>

                <SocialIcon color="E60023">
                    <LinkedIn />
                </SocialIcon>

            </SocialContainer>
        </Left>

        <Center>

            <Title>Useful Links</Title>
            <List>
                <ListItem>Home</ListItem>
                <ListItem>Card</ListItem>
                <ListItem>Man Fashion</ListItem>
                <ListItem>Woman Fashion</ListItem>
                <ListItem>Accessories</ListItem>
                <ListItem>My Account</ListItem>
                <ListItem>Order Tracking</ListItem>
                <ListItem>Whislist</ListItem>
                <ListItem>Terms</ListItem>
                <ListItem>Winter</ListItem>

            </List>

        </Center>

        <Right></Right>
        
    </Container>
  )
}

export default Footer
