import React from 'react'
import styled from 'styled-components'
import Announcement from '../compenents/Announcement'
import Footer from '../compenents/Footer'
import Navbar from '../compenents/Navbar'
import NewsLetter from '../compenents/NewsLetter'

const Container = styled.div`

`

const Wrapper = styled.div`
  padding: 50px;
  display: flex;
`

const ImageContainer = styled.div`
  flex:1;
`

const Image = styled.img`
  width: 100%;
  height: 90vh;
  object-fit: cover;
`

const InfoContainer = styled.div`
  flex: 1;
  padding: 0px 50px;
`

const Title = styled.h1`
  font-weight: 200;
`

const Desc = styled.p`
  margin: 20px 0px;
`

const Price = styled.span`
  font-weight: 100;
  font-size: 40px;
`

const FilterContainer = styled.div`
  width: 50%;
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
`

const Filter = styled.div`
  display: flex;
  align-items: center;
`

const FilterTitle = styled.span`
  font-size: 20px;
  font-weight: 200;
`

const FilterColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${ props => props.color };
  margin: 0px 5px;
  cursor: pointer;
`

const FilterSize = styled.select`
  margin-left: 10px;
  padding: 5px;
`

const FilterSizeOption = styled.option`

`





function Product() {
  return (
    <Container>
        <Announcement />
        <Navbar />

        <Wrapper>

            <ImageContainer>

              <Image src="https://i.ibb.co/S6qMxwr/jean.jpg" />
                
            </ImageContainer>

            <InfoContainer>

              <Title>Lorem BlaBla</Title>
                <Desc>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Id molestiae enim velit impedit accusamus modi, illo neque, aliquid atque assumenda a nobis. Repellendus hic repellat sit assumenda aut nobis nostrum.</Desc>
                <Price>$ 20</Price>

                <FilterContainer>

                  <Filter>

                    <FilterTitle>Color:</FilterTitle>
                    <FilterColor color="black"></FilterColor>
                    <FilterColor color="darkblue"></FilterColor>
                    <FilterColor color="gray"></FilterColor>

                  </Filter>

                  <Filter>

                    <FilterTitle>Size</FilterTitle>

                    <FilterSize>

                      <FilterSizeOption>XS</FilterSizeOption>
                      <FilterSizeOption>S</FilterSizeOption>
                      <FilterSizeOption>M</FilterSizeOption>
                      <FilterSizeOption>L</FilterSizeOption>
                      <FilterSizeOption>XL</FilterSizeOption>


                    </FilterSize>

                  </Filter>

                </FilterContainer>

            </InfoContainer>

        </Wrapper>


        <NewsLetter />
        <Footer/>
    </Container>
  )
}

export default Product
